mapper.select:
SELECT COUNT(*) from boards


	WHERE B_LIKECOUNT >= 1;


	WHERE b_category = #{category}

SELECT * from boards 


	WHERE B_LIKECOUNT >= 1


	WHERE b_category = #{category}


ORDER BY b_no DESC limit #{start} , #{cntPerPage}

SELECT * from boards 
WHERE B_WRITER = #{writer}
ORDER BY b_no DESC limit #{pvo.start} , #{pvo.cntPerPage}
